INSERT INTO `videos` (`id`, `topic_id`, `youtube_id`, `title`, `description`, `duration`, `source_src`, `source_type`, `thumbnail_src`, `thumbnail_media`, `thumbnail_type`, `channel_id`, `published_at`, `playlist_id`, `created_at`, `updated_at`) VALUES (257, 257, 'ec3m3qQntVA', 'Impressora 3D Simples | Parte 1 | Como fazer uma impressora 3D mais barata do que a impressora comum', 'Projeto BT4\nImpressora 3D simples\nVolume de impressão: 140x130x100 (comprimento x largura x altura em mm)\n\nArquivos:\n\nhttps://drive.google.com/drive/folders/0B69KEXPVHfbSMl9Rc2d6U1J4ZGM?usp=sharing\n\nQuanto custa uma Impressora 3D?\nAlguns exemplos.\n\nAs impressoras comuns são bem mais baratas.\nA produção em larga escala reduz o custo.\n\nQuanto custa uma impressora auto-replicável?\nAs peças plásticas são caras.\n\nE fazer uma impressora simples?\nBem básica.\n\nÉ possível fazer uma impressora 3D pelo custo de uma impressora comum?\nPor exemplo, usando peças padrões e adaptando o motor.\n\nLista de peças.\nCusto estimado $50.\n\nEste é o projeto BT4.\n\nLista de peças:\nArduino Mega 2560  1 pc\nRamps 1.4  1 pc\nStepper driver A4988  4 pc\nStepper motor 28BYJ-48 5V  4 pc\nPower supply   12V, 10A 1 pc\nPulley GT2 20 teeth, inner Ø5mm 2 pc\nBelt GT2 0,5m, T2.5 0,5 m\nBearing 624ZZ Ø13x5mm, Ø4mm 2 pc\nBearing LM8UU Ø15x24mm, Ø8mm 9 pc\nSmooth rod  Stainless D8, 250mm, Ø8mm 6 pc\nThreaded rod  Stainless M5, 250mm, Ø5mm 1 pc\nEndstop  Microswitch 3 pc\nHotend E3D V5 1.75mm filament, 0.4mm nozzle 1 pc\nFan cooler  12V, 30x30x10mm 1 pc\nFan Housing E3D V5  1 pc\nPTFE Tube  Outter Ø4mm, inner Ø3mm, 1m\nDriver gear MK8   1 pc', '00:00:00', 'ec3m3qQntVA', 'video/youtube', 'https://i.ytimg.com/vi/ec3m3qQntVA/mqdefault.jpg', '(min-width: 400px;)', 'image/jpeg', 'UC8nhvBNtxW3xy68aKJnCFGQ', '2019-05-07 01:13:12', 108, '2022-08-15 15:08:36', '2022-08-15 15:08:36');
INSERT INTO `videos` (`id`, `topic_id`, `youtube_id`, `title`, `description`, `duration`, `source_src`, `source_type`, `thumbnail_src`, `thumbnail_media`, `thumbnail_type`, `channel_id`, `published_at`, `playlist_id`, `created_at`, `updated_at`) VALUES (258, 258, 'afNIlMh4ecw', 'Impressora 3D  Simples | Parte 2 | Como instalar e testar o Arduino | Mega | ATmega 2560', 'Projeto BT4.\n\nTestando o Arduino.\n\nO LED pisca a cada um segundo.\n\nEste aplicativo altera a programação do Arduino.\n\nAlterando o intervalo para cinco segundos.\n\nMontagem do escudo e driver do motor.', '00:00:00', 'afNIlMh4ecw', 'video/youtube', 'https://i.ytimg.com/vi/afNIlMh4ecw/mqdefault.jpg', '(min-width: 400px;)', 'image/jpeg', 'UC8nhvBNtxW3xy68aKJnCFGQ', '2019-05-07 01:14:16', 108, '2022-08-15 15:08:36', '2022-08-15 15:08:36');
