INSERT INTO `videos` (`id`, `topic_id`, `youtube_id`, `title`, `description`, `duration`, `source_src`, `source_type`, `thumbnail_src`, `thumbnail_media`, `thumbnail_type`, `channel_id`, `published_at`, `playlist_id`, `created_at`, `updated_at`) VALUES (272, 272, 'eUf5-UCVueU', 'Large scale DIY 3D printer - 1200 x 1200mm print bed - Build progress and test print preview', 'Design download, and more information can be found at:\nhttp://www.thingiverse.com/thing:1977727\n\nYou can also check us out on Facebook:\nhttps://www.facebook.com/Sub33D/\n\nFilament for the printer is created on our own modified Lyman / Mulier version 5 Filament Extruder:\nhttps://www.youtube.com/watch?v=RwAdB6rVrKg\n\nThis printer was designed to print very large scale using recycled ABS plastic (we also recycle, shred and produce recycled ABS filament).\nMost of the other very large scale printers out there tend to print in PLA which is a lot less problematic at large scale.\n\nMain features:\n- Build volume 1200mm x 1200mm x 600mm\n- H bot layout with fixed heat-bed (4 x z-axis motors to move the light weight H frame rather than the insanely heavy heat-bed)\n- 1mm nozzle, double volcano extruder hot end to increase flow rate (80W total heater power)\n- 2400W heatbed using 4 x 230V 600W 400mm x 400mm silicone heating pads (controlled with 25A solid state relay)\n- 2 x Recycled Nema23 stepper motors for y-axis, driven by single 5A CNC type stepper driver\n- 4 x Recycled Nema23 stepper motors for z-axis, driven by single 5A CNC type stepper driver\n- 1 x Recycled Nema23 stepper motor for x-axis, driven by standard 2.5A stepper driver\n- 1 x Recycled Nema17 stepper motor for extruder, driven by standard 2.5A stepper driver\n- Designed in Sketchup Make 2017, design file included in thingiverse link downloads (Sub33D v3.07.09.skp)\n- Frame built from 50mm x 50mm x 2mm box section aluminium\n- Total build cost is close to $2000 AUD\n- Marlin-RC firmware also included in thingiverse link downloads (Marlin-RC.zip)\n\nIf anyone is thinking to build something like this I would recommend using a 12mm heat-bed plate (rather than 6mm) as the aluminium plate deforms up to 5mm when heated to 90 deg C, I am using 9 x 9 point auto-bed leveling to pick up the deformation shape).\nAlso the 400mm x 400m 600W silicone heaters are a little small as the distance between them causes around a 10 deg C difference on the heat-bed surface between the heaters and above them).\nI\'m Currently fitting 4 x 150W IR heaters to reduce heat-bed deformation and maintain consistent heat on printed ABS parts.\n\nSpecial thanks to Substation33 for all the support and recycled materials.', '00:00:00', 'eUf5-UCVueU', 'video/youtube', 'https://i.ytimg.com/vi/eUf5-UCVueU/mqdefault.jpg', '(min-width: 400px;)', 'image/jpeg', 'UC8nhvBNtxW3xy68aKJnCFGQ', '2019-05-09 12:04:23', 108, '2022-08-15 15:08:37', '2022-08-15 15:08:37');
INSERT INTO `videos` (`id`, `topic_id`, `youtube_id`, `title`, `description`, `duration`, `source_src`, `source_type`, `thumbnail_src`, `thumbnail_media`, `thumbnail_type`, `channel_id`, `published_at`, `playlist_id`, `created_at`, `updated_at`) VALUES (273, 273, 'SGy_qPNHek4', 'How to make 3D printer', 'Get cost effective stepper motors from Stepperonline  https://www.omc-stepperonline.com/?tracking=sharma  \n\n\n\nHello guys I have made this cheap 3D printer at home, I already have a 3D printer but I want to make my own \nSo i design each and every part of this printer as per my need and build my own 3D Printer\n\nPlease note this before printing !!\n\nThe total printing time for all parts are approx 30 hour PLA 1.75\nI have used 6MM Smooth rod & T8 lead screw with NEMA 17 Motor 42x42x34mm 4 Wires and GT2 timming belt \n\nIf your Hardware configuration match with above you can use those 3D model to build your 3d printer\n\nDownload 3D files :- \nhttps://www.thingiverse.com/thing:2838187\n\n\nMaterial I Used\n3D printer electronics kit :- https://amzn.to/2pB685v\n6MM Smooth rod :- https://amzn.to/2pAWizG\nT8 Threaded rod :-  https://amzn.to/2pAEC83\nStepper motor :-    https://goo.gl/w9hHG3\nTiming belt :- https://amzn.to/2pB4Wz3\nPulley :- https://amzn.to/2G6OIUG\nIdler pulley :- https://amzn.to/2pAw94N\nHot end :- https://amzn.to/2G3exsI\nExtruder :- https://amzn.to/2GgA00F\nSMPS :-  https://amzn.to/2pAQ8QS\n20 x 20 aluminium frame\n\n\nMarlin Firmware :-\nhttps://github.com/MarlinFirmware/Marlin/archive/1.1.x.zip\n\nPronterface :-\nhttp://kliment.kapsi.fi/printrun/\n\n\n\nvisit for more\nhttp://electricdiylab.com/how-to-make-3d-printer-diy-3d-printer/\n\nFB page :- https://www.facebook.com/InnovativeMr/\n\n\nMy Gears :- \nCamera :- http://amzn.to/2FO4W5E\n\nVideo Light :- http://amzn.to/2FNKzFL\n\nMic :- http://amzn.to/2HYqBZC\n\nSoldering Kit :-  http://amzn.to/2FgNd8Z\n\nScrew driver set :- http://amzn.to/2FkhHHm\n\nRotary tool :- http://amzn.to/2FM3Lnh This video is made for entertainment purposes only. Please use judgment, care, and precaution if you plan to replicate.', '00:00:00', 'SGy_qPNHek4', 'video/youtube', 'https://i.ytimg.com/vi/SGy_qPNHek4/mqdefault.jpg', '(min-width: 400px;)', 'image/jpeg', 'UC8nhvBNtxW3xy68aKJnCFGQ', '2019-05-09 12:08:37', 108, '2022-08-15 15:08:37', '2022-08-15 15:08:37');
