INSERT INTO `videos` (`id`, `topic_id`, `youtube_id`, `title`, `description`, `duration`, `source_src`, `source_type`, `thumbnail_src`, `thumbnail_media`, `thumbnail_type`, `channel_id`, `published_at`, `playlist_id`, `created_at`, `updated_at`) VALUES (276, 276, '1il-3_FJtA8', 'Building a 3D Printer From CD Drives', 'In this video, I will be building a Tinkernut’s DIY 3d printer cnc machine from scratch for under $50 using a 3d printing pen, Cd-Rom drives, an Arduino, and stepper motor drivers. \n\nThis homemade 3d printer was a lot of fun to make and didn\'t take to long to build.\n\n►The original designer of this 3D printer (Tinkernut): \nhttps://www.youtube.com/watch?v=anIy6eb1fW0\n\n►How to build this 3d printer: https://www.youtube.com/watch?v=3p8BP7B7Pmw\n\n▼PARTS LIST:\n\n2 Wii Optical Drives $0: Got these from broken Wii consoles. \n\nA Dell Computer Cd Drive $0: Got from old broken computer.\n\nA 3D Printing Pen $18: https://rover.ebay.com/rover/1/711-53200-19255-0/1?icep_id=114&ipn=icep&toolid=20004&campid=5338052152&mpre=http%3A%2F%2Fwww.ebay.com%2Fitm%2FKids-Gifts-3D-Printing-Drawing-Pen-Modeling-ABS-PLA-Filaments-Arts-Printer-Tool%2F302458568818%3F_trkparms%3Daid%253D222007%2526algo%253DSIM.MBE%2526ao%253D1%2526asc%253D41376%2526meid%253D12e435e1ab154f50a948c8a0e535eade%2526pid%253D100011%2526rk%253D5%2526rkt%253D12%2526sd%253D192068489993%26_trksid%3Dp2047675.c100011.m1850\n\nAn Arduino Uno R3 Knock Off $12: https://rover.ebay.com/rover/1/711-53200-19255-0/1?icep_id=114&ipn=icep&toolid=20004&campid=5338052152&mpre=http%3A%2F%2Fwww.ebay.com%2Fitm%2FUNO-R3-Board-ATmega328P-ATmega16U2-with-USB-Cable-for-Arduino-%2F191788787629%3Fhash%3Ditem2ca780a7ad%3Ag%3AFHoAAOSwHQ9WXUw-%26rmvSB%3Dtrue\n\n3 Stepper Motor Drivers $15: https://rover.ebay.com/rover/1/711-53200-19255-0/1?icep_id=114&ipn=icep&toolid=20004&campid=5338052152&mpre=http%3A%2F%2Fwww.ebay.com%2Fitm%2FSchmalzhaus-EasyDriver-Shield-stepping-Stepper-Motor-Driver-V4-A3967-For-Arduino-%2F331178938895%3Fhash%3Ditem4d1bcd920f%3Ag%3AtREAAOxySoJTTpj2%26rmvSB%3Dtrue\n\nTotal = $45\n\nAll of the ebay links are affiliate links, so when you buy something on ebay that I linked to I get a small portion of the money.\n\nElectronic Grenade', '00:00:00', '1il-3_FJtA8', 'video/youtube', 'https://i.ytimg.com/vi/1il-3_FJtA8/mqdefault.jpg', '(min-width: 400px;)', 'image/jpeg', 'UC8nhvBNtxW3xy68aKJnCFGQ', '2019-05-10 12:08:36', 108, '2022-08-15 15:08:37', '2022-08-15 15:08:37');
